import React from "react";
import { motion } from "framer-motion";

const services = [
  { title: "Daycare (12 hrs)", desc: "Perfect for working pet parents.", icon: "‚è∞" },
  { title: "Boarding (24 hrs)", desc: "Safe overnight stay with care.", icon: "üè†" },
  { title: "Long Stay", desc: "Weekly or monthly stay with updates.", icon: "üìÜ" },
  { title: "Food & Custom Meals", desc: "Chicken, Rice & more (customizable).", icon: "üçñ" },
];

function Services() {
  return (
    <section id="services" className="py-20 bg-green-50 text-center">
      <h2 className="text-3xl font-bold text-green-800 mb-10">Our Services</h2>
      <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-4 max-w-6xl mx-auto px-6">
        {services.map((service, index) => (
          <motion.div
            key={index}
            className="bg-white shadow-lg rounded-2xl p-6 hover:shadow-2xl transition"
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.6, delay: index * 0.2 }}
          >
            <div className="text-4xl">{service.icon}</div>
            <h3 className="text-xl font-semibold mt-4">{service.title}</h3>
            <p className="text-gray-600 mt-2">{service.desc}</p>
          </motion.div>
        ))}
      </div>
      <p className="mt-6 text-green-700 font-medium">üìå Pricing available on request.</p>
    </section>
  );
}

export default Services;
