import React, { useState } from "react";
import siteData from "../siteData";

const navItems = [
  { href: "#home", label: "Home" },
  { href: "#about", label: "About" },
  { href: "#services", label: "Services" },
  { href: "#gallery", label: "Gallery" },
  { href: "#reviews", label: "Reviews" },
  { href: "#contact", label: "Contact" },
];

export default function Navbar() {
  const [open, setOpen] = useState(false);

  return (
    <nav className="fixed top-0 inset-x-0 z-50 bg-green-700/95 backdrop-blur text-white shadow">
      <div className="max-w-6xl mx-auto px-4 py-3 flex items-center justify-between">
        <a href="#home" className="flex items-center gap-2 font-extrabold text-xl">
          <span className="text-2xl">üêæ</span>
          <span>{siteData.businessName}</span>
        </a>

        {/* Desktop */}
        <div className="hidden md:flex items-center gap-6">
          {navItems.map((n) => (
            <a
              key={n.href}
              href={n.href}
              className="hover:text-yellow-300 transition-colors"
            >
              {n.label}
            </a>
          ))}
        </div>

        {/* Mobile menu button */}
        <button
          className="md:hidden inline-flex items-center justify-center rounded-md px-3 py-2 hover:bg-green-600"
          aria-label="Toggle Menu"
          onClick={() => setOpen((v) => !v)}
        >
          ‚ò∞
        </button>
      </div>

      {/* Mobile panel */}
      {open && (
        <div className="md:hidden border-t border-green-600">
          <div className="px-4 py-3 flex flex-col gap-3">
            {navItems.map((n) => (
              <a
                key={n.href}
                href={n.href}
                className="py-2 rounded hover:bg-green-600"
                onClick={() => setOpen(false)}
              >
                {n.label}
              </a>
            ))}
          </div>
        </div>
      )}
    </nav>
  );
}
