import React from "react";
import { motion } from "framer-motion";

const services = [
  {
    icon: "‚è∞",
    title: "Daycare (12 hrs)",
    desc: "Drop in before work, pick up after. Play, rest & care all day.",
  },
  {
    icon: "üè†",
    title: "Boarding (24 hrs)",
    desc: "Overnight stays in a safe, homely environment with supervision.",
  },
  {
    icon: "üìÜ",
    title: "Long Stay",
    desc: "Weekly or monthly stays with regular photo/video updates.",
  },
  {
    icon: "üç≤",
    title: "Food & Custom Meals",
    desc: "Chicken & Rice by default ‚Äî can be customized for your pet.",
  },
  {
    icon: "üåø",
    title: "Open & Cage Areas",
    desc: "Both cage and open-space options based on temperament.",
  },
  {
    icon: "üõù",
    title: "Play & Safe Area",
    desc: "Green, fenced play zones with mountain views.",
  },
  {
    icon: "üöñ",
    title: "Pick & Drop Service",
    desc: "Convenient paid transport to safely bring your pet to and from Bela‚Äôs Paw‚Äôs.",
  },
];

export default function Services() {
  return (
    <section id="services" className="section-anchor py-20 bg-gray-50">
      <div className="max-w-6xl mx-auto px-6">
        <h2 className="text-3xl md:text-4xl font-extrabold text-green-800 text-center">
          Our Services
        </h2>
        <p className="text-center text-gray-600 mt-2">
          Everything your dog needs for a comfortable, happy stay.
        </p>

        <div className="mt-10 grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {services.map((s, i) => (
            <motion.div
              key={s.title}
              initial={{ opacity: 0, y: 24 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5, delay: i * 0.08 }}
              className="bg-white rounded-2xl shadow-md 
                         hover:bg-gradient-to-br hover:from-green-50 hover:to-green-100
                         transition transform hover:-translate-y-2 hover:scale-[1.02] 
                         hover:shadow-xl p-6 text-center"
            >
              <div className="text-4xl">{s.icon}</div>
              <h3 className="mt-3 font-semibold text-lg text-gray-800">{s.title}</h3>
              <p className="mt-2 text-gray-600">{s.desc}</p>
            </motion.div>
          ))}
        </div>

        <p className="mt-8 text-center text-green-700 font-medium">
          üìå Pricing available on request.
        </p>
      </div>
    </section>
  );
}
