import React, { useState } from "react";
import siteData from "../siteData";

export default function FloatingButtons() {
  const [showModal, setShowModal] = useState(false);

  return (
    <>
      {/* Floating Buttons */}
      <div className="fixed bottom-6 right-6 flex flex-col gap-3 z-50">
        <button
          onClick={() => setShowModal(true)}
          className="bg-pink-600 text-white px-5 py-2.5 rounded-full shadow-lg hover:scale-105 active:scale-95 transition"
        >
          ðŸ“ž Call / ðŸ’¬ WhatsApp
        </button>
      </div>

      {/* Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-[100]">
          <div className="bg-white rounded-2xl shadow-xl p-6 w-80 text-center relative">
            <h3 className="text-xl font-bold text-gray-800 mb-4">Contact Us</h3>

            {/* Sagar */}
            <div className="mb-4">
              <p className="font-semibold">ðŸ“ž Sagar</p>
              <a
                href={`tel:${siteData.phones[0]}`}
                className="block mt-2 rounded bg-pink-600 text-white px-4 py-2 hover:bg-pink-700"
              >
                Call Sagar
              </a>
              <a
                href={`https://wa.me/${siteData.phones[0].replace(/\D/g, "")}`}
                target="_blank"
                rel="noreferrer"
                className="block mt-2 rounded bg-green-600 text-white px-4 py-2 hover:bg-green-700"
              >
                WhatsApp Sagar
              </a>
            </div>

            {/* Madhav */}
            <div className="mb-4">
              <p className="font-semibold">ðŸ“ž Madhav</p>
              <a
                href={`tel:${siteData.phones[1]}`}
                className="block mt-2 rounded bg-pink-600 text-white px-4 py-2 hover:bg-pink-700"
              >
                Call Madhav
              </a>
              <a
                href={`https://wa.me/${siteData.phones[1].replace(/\D/g, "")}`}
                target="_blank"
                rel="noreferrer"
                className="block mt-2 rounded bg-green-600 text-white px-4 py-2 hover:bg-green-700"
              >
                WhatsApp Madhav
              </a>
            </div>

            {/* Close Button */}
            <button
              onClick={() => setShowModal(false)}
              className="mt-4 rounded-full bg-gray-300 text-gray-700 px-5 py-2 hover:bg-gray-400"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </>
  );
}
