  return (
    <section className="relative h-[80vh] flex items-center justify-center text-center bg-gradient-to-r from-green-700 to-green-500 text-white overflow-hidden">
      {/* Background Image Overlay */}
      <div className="absolute inset-0">
        <img
          src="https://images.unsplash.com/photo-1601979031896-03a5cf1d6a52?auto=format&fit=crop&w=1400&q=80"
          alt="Happy Dog"
          className="w-full h-full object-cover opacity-40"
        />
      </div>

      {/* Content */}
      <motion.div
        className="relative z-10 max-w-3xl px-6"
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        <h1 className="text-5xl md:text-6xl font-extrabold leading-tight drop-shadow-lg">
          üêæ Safe, Happy & Loving Stay <br /> for Your Pets
        </h1>
        <p className="mt-4 text-lg md:text-xl font-light">
          At Bela's Paw‚Äôs Pet Home Stay & Caf√©, your furry friends are treated like family ‚ù§Ô∏è
        </p>

        {/* Buttons */}
        <div className="mt-8 flex justify-center gap-4">
          <a
            href="tel:+911234567890"
            className="bg-pink-600 hover:bg-pink-700 text-white px-6 py-3 rounded-full shadow-lg font-semibold transition transform hover:scale-105"
          >
            üìû Call Now
          </a>
          <a
            href="https://wa.me/911234567890"
            className="bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-full shadow-lg font-semibold transition transform hover:scale-105"
          >
            üí¨ WhatsApp
          </a>
        </div>
      </motion.div>
    </section>
  );
}

export default Hero;
