import React from "react";
import { motion } from "framer-motion";
import siteData from "../siteData";

export default function Home() {
  const [p1, p2] = siteData.phones;

  return (
    <section id="home" className="section-anchor pt-24">
      <div className="relative h-[78vh] min-h-[520px] w-full overflow-hidden">
        {/* Background image */}
        <img
          src="https://images.unsplash.com/photo-1507146426996-ef05306b995a?q=80&w=1600&auto=format&fit=crop"
          alt="Happy dog close-up"
          className="absolute inset-0 w-full h-full object-cover"
        />
        {/* Overlay */}
        <div className="absolute inset-0 bg-black/45" aria-hidden />

        {/* Content */}
        <div className="relative z-10 h-full max-w-5xl mx-auto flex flex-col items-center justify-center px-6 text-center text-white">
          <motion.h1
            initial={{ opacity: 0, y: 24 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.7 }}
            className="text-4xl md:text-6xl font-extrabold leading-tight"
          >
            Safe, Happy & Loving Stay for Your Pets
          </motion.h1>
          <motion.p
            initial={{ opacity: 0, y: 16 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.2, duration: 0.6 }}
            className="mt-4 text-lg md:text-xl"
          >
            At {siteData.businessName} ‚Äî {siteData.tagline} üê∂‚ú®
          </motion.p>

          <motion.div
            initial={{ opacity: 0, y: 12 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.35, duration: 0.5 }}
            className="mt-8 flex flex-wrap gap-4"
          >
            <a
              href={`tel:${p1}`}
              className="rounded-full bg-pink-600 px-6 py-3 font-semibold shadow hover:scale-105 active:scale-95 transition"
            >
              üìû Call Now
            </a>
            <a
              href={`https://wa.me/${p2.replace(/\D/g, "")}`}
              target="_blank"
              rel="noreferrer"
              className="rounded-full bg-green-500 px-6 py-3 font-semibold shadow hover:scale-105 active:scale-95 transition"
            >
              üí¨ WhatsApp
            </a>
          </motion.div>
        </div>
      </div>
    </section>
  );
}
