import React from "react";
import { motion } from "framer-motion";

export default function About() {
  return (
    <section id="about" className="section-anchor py-20">
      <div className="max-w-6xl mx-auto px-6 grid md:grid-cols-2 gap-10 items-center">
        <motion.div
          initial={{ opacity: 0, x: -24 }}
          whileInView={{ opacity: 1, x: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
          className="order-2 md:order-1"
        >
          <h2 className="text-3xl md:text-4xl font-extrabold text-green-800">
            Welcome to Bela’s Paw’s
          </h2>
          <p className="mt-4 text-gray-700 leading-7">
            Run by two brothers, Bela’s Paw’s began during the lockdown while
            fostering and caring for rescue dogs. With support from PFA, they
            helped <strong>25–30 dogs</strong> from different parts of Pune —
            providing medical care, proper food, and lots of love. One rescued
            Doberman, once undernourished and used for breeding, is now healthy,
            happy, and part of the family.  
          </p>
          <p className="mt-4 text-gray-700 leading-7">
            In 2024 they started this <em>Pet Home Stay & Café</em> so your
            furry friend can enjoy a safe, green, mountain-side space with daily
            updates, playtime, and home-style food.
          </p>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, x: 24 }}
          whileInView={{ opacity: 1, x: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6, delay: 0.1 }}
          className="order-1 md:order-2"
        >
          <img
            className="rounded-2xl shadow-lg w-full object-cover h-80"
            src="https://images.unsplash.com/photo-1601758124069-cbc1e6dfc4f0?q=80&w=1200&auto=format&fit=crop"
            alt="Dogs playing in a green open space"
          />
        </motion.div>
      </div>
    </section>
  );
}
