// src/components/Navbar.js
import React, { useState } from "react";
import site from "../siteData";

const links = [
  { href: "#about", label: "About" },
  { href: "#services", label: "Services" },
  { href: "#gallery", label: "Gallery" },
  { href: "#reviews", label: "Reviews" },
  { href: "#contact", label: "Contact" },
];

export default function Navbar() {
  const [open, setOpen] = useState(false);
  return (
    <header className="sticky top-0 z-50 bg-white/90 backdrop-blur border-b border-stone-200">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 h-16 flex items-center justify-between">
        <a href="#top" className="flex items-center gap-2">
          <span className="text-2xl">üêæ</span>
          <div className="leading-tight">
            <div className="font-extrabold text-lg">{site.brand}</div>
            <div className="text-[11px] text-stone-500 -mt-1">{site.tagline}</div>
          </div>
        </a>

        {/* Desktop */}
        <nav className="hidden md:flex items-center gap-6">
          {links.map((l) => (
            <a key={l.href} href={l.href} className="text-stone-700 hover:text-green-700">
              {l.label}
            </a>
          ))}
          <a href={`tel:${site.phones[0]}`} className="btn btn-primary">Call</a>
        </nav>

        {/* Mobile */}
        <button
          className="md:hidden p-2 rounded-lg border border-stone-300"
          onClick={() => setOpen((v) => !v)}
          aria-label="Toggle menu"
        >
          ‚ò∞
        </button>
      </div>

      {/* Mobile menu */}
      {open && (
        <div className="md:hidden border-t border-stone-200 bg-white">
          <div className="px-4 py-3 flex flex-col gap-3">
            {links.map((l) => (
              <a
                key={l.href}
                href={l.href}
                onClick={() => setOpen(false)}
                className="text-stone-700"
              >
                {l.label}
              </a>
            ))}
            <a href={`tel:${site.phones[0]}`} className="btn btn-primary justify-center">
              Call Now
            </a>
          </div>
        </div>
      )}
    </header>
  );
}
