// Services.js
import React from "react";
import { motion } from "framer-motion";
import { PawPrint, Clock, Home, Utensils } from "lucide-react";

function Services() {
  const services = [
    {
      icon: <Clock className="w-10 h-10 text-green-600" />,
      title: "Daycare (12 hr)",
      desc: "Perfect for busy pet parents! Safe playtime, naps & care for your pets while you‚Äôre away.",
    },
    {
      icon: <Home className="w-10 h-10 text-green-600" />,
      title: "Boarding (24 hr)",
      desc: "A cozy overnight stay with round-the-clock love, walks & feeding.",
    },
    {
      icon: <PawPrint className="w-10 h-10 text-green-600" />,
      title: "Long Stay (Weekly / Monthly)",
      desc: "Extended stays with personalized care plans, daily walks, and lots of cuddles.",
    },
    {
      icon: <Utensils className="w-10 h-10 text-green-600" />,
      title: "Food: Chicken & Rice",
      desc: "Nutritious home-style meals. Customizable diet options available for your pets.",
    },
  ];

  return (
    <section className="py-16 px-6 bg-green-50">
      <div className="max-w-6xl mx-auto text-center">
        <h2 className="text-4xl font-extrabold text-green-800">
          üê∂ Our Services
        </h2>
        <p className="mt-3 text-gray-600">
          Because your pets deserve comfort, safety, and happiness.
        </p>

        {/* Service Cards */}
        <div className="mt-10 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-8">
          {services.map((service, index) => (
            <motion.div
              key={index}
              whileHover={{ scale: 1.05 }}
              className="bg-white shadow-lg rounded-2xl p-6 flex flex-col items-center text-center hover:shadow-2xl transition"
            >
              {service.icon}
              <h3 className="mt-4 text-xl font-semibold text-green-700">
                {service.title}
              </h3>
              <p className="mt-2 text-gray-600 text-sm">{service.desc}</p>
            </motion.div>
          ))}
        </div>

        {/* Pricing */}
        <motion.p
          className="mt-10 text-green-700 font-medium"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.3 }}
        >
          üìå Pricing available on request.
        </motion.p>
      </div>
    </section>
  );
}

export default Services;
